using System;
class Game { static void Main(string[] args) { Console.WriteLine("Enter First Num"); double First, Second; First = double.Parse(Console.ReadLine()); Console.WriteLine("Enter Second Num"); Second = double.Parse(Console.ReadLine()); char sign; Console.WriteLine("Enter Sign"); sign = char.Parse(Console.ReadLine()); if (sign == '+') { Console.WriteLine(First + Second); } else if (sign == '-') { Console.WriteLine(First - Second); } else if (sign == '*') { Console.WriteLine(First * Second); } else if (sign == '/') { if (Second == 0) { Console.WriteLine("Division by 0 is not allowed"); } else { Console.WriteLine(First / Second); } } } }
